mapper.select:
		SELECT 
			id, custom_id, talk_date, record_or_plan, insert_time, insert_operator, update_time, update_operator 		
		FROM t_fc_custom_follow_plan 



		  		  	AND id = #{id}   


		  		  	AND custom_id = #{customId}   


		  		  	AND talk_date LIKE '%${ talkDate}%'  


		  		  	AND record_or_plan LIKE '%${ recordOrPlan}%'  


		  		  	AND insert_time = #{insertTime}   


		  		  	AND insert_operator = #{insertOperator}   


		  		  	AND update_time = #{updateTime}   


		  		  	AND update_operator = #{updateOperator}   





                ORDER BY ${sort} ${order}


                ORDER BY id DESC

select 
	*  
from t_fc_custom_follow_plan



  		  	AND id = #{id}   


  		  	AND custom_id = #{customId}   


  		  	AND talk_date LIKE '%${ talkDate}%'  


  		  	AND record_or_plan LIKE '%${ recordOrPlan}%'  


  		  	AND insert_time = #{insertTime}   


  		  	AND insert_operator = #{insertOperator}   


  		  	AND update_time = #{updateTime}   


  		  	AND update_operator = #{updateOperator}

mapper.update:
update t_fc_custom_follow_plan
custom_id = #{customId}, talk_date = #{talkDate}, record_or_plan = #{recordOrPlan}, insert_time = #{insertTime}, insert_operator = #{insertOperator}, update_time = #{updateTime}, update_operator = #{updateOperator}
where id = #{id}
