mapper.select:
select
id,name,value,type,description,sort,parent_id,create_by,create_date,update_by,update_date,remarks,del_flag
from sys_dict

    and id = #{id}
    and name = #{name}
    and value = #{value}
    and type = #{type}
    and description = #{description}
    and sort = #{sort}
    and parent_id = #{parentId}
    and create_by = #{createBy}
    and create_date = #{createDate}
    and update_by = #{updateBy}
    and update_date = #{updateDate}
    and remarks = #{remarks}
    and del_flag = #{delFlag}



        order by ${sort} ${order}


        order by id desc

select count(*) from sys_dict

    and id = #{id}
    and name = #{name}
    and value = #{value}
    and type = #{type}
    and description = #{description}
    and sort = #{sort}
    and parent_id = #{parentId}
    and create_by = #{createBy}
    and create_date = #{createDate}
    and update_by = #{updateBy}
    and update_date = #{updateDate}
    and remarks = #{remarks}
    and del_flag = #{delFlag}

mapper.update:
update sys_dict
name = #{name, jdbcType=VARCHAR}, value = #{value, jdbcType=VARCHAR}, type = #{type, jdbcType=VARCHAR}, description = #{description, jdbcType=VARCHAR}, sort = #{sort, jdbcType=VARCHAR}, parent_id = #{parentId, jdbcType=VARCHAR}, create_by = #{createBy, jdbcType=VARCHAR}, create_date = #{createDate, jdbcType=VARCHAR}, update_by = #{updateBy, jdbcType=VARCHAR}, update_date = #{updateDate, jdbcType=VARCHAR}, remarks = #{remarks, jdbcType=VARCHAR}, del_flag = #{delFlag, jdbcType=VARCHAR}
where id = #{id}
