mapper.select:
		SELECT 
			id, dept_id, course_name, price, course_detail 		
		FROM t_course 



		  		  	AND id = #{id}   


		  		  	AND dept_id LIKE '%${ deptId}%'  


		  		  	AND course_name LIKE '%${ courseName}%'  


		  		  	AND price = #{price}   


		  		  	AND course_detail LIKE '%${ courseDetail}%'  





                ORDER BY ${sort} ${order}


                ORDER BY id DESC

select * from t_course
AND id = #{id}
AND dept_id LIKE '%${ deptId}%'
AND course_name LIKE '%${ courseName}%'
AND price = #{price}
AND course_detail LIKE '%${ courseDetail}%'

mapper.update:
update t_course 

	dept_id = #{deptId}, 
	course_name = #{courseName}, 
	price = #{price}, 
	course_detail = #{courseDetail}

where id = #{id}
