mapper.select:
		SELECT 
			id, dept_id, package_name, subTitle, course_ids, goods_ids, total_price 		
		FROM t_course_packages 



		  		  	AND id = #{id}   


		  		  	AND dept_id = #{deptId}   


		  		  	AND package_name LIKE '%${ packageName}%'  


		  		  	AND subTitle LIKE '%${ subtitle}%'  


		  		  	AND course_ids LIKE '%${ courseIds}%'  


		  		  	AND goods_ids LIKE '%${ goodsIds}%'  


		  		  	AND total_price = #{totalPrice}   





                ORDER BY ${sort} ${order}


                ORDER BY id DESC

select 
	*  
from t_course_packages



  		  	AND id = #{id}   


  		  	AND dept_id = #{deptId}   


  		  	AND package_name LIKE '%${ packageName}%'  


  		  	AND subTitle LIKE '%${ subtitle}%'  


  		  	AND course_ids LIKE '%${ courseIds}%'  


  		  	AND goods_ids LIKE '%${ goodsIds}%'  


  		  	AND total_price = #{totalPrice}

mapper.update:
update t_course_packages
dept_id = #{deptId}, package_name = #{packageName}, subTitle = #{subtitle}, course_ids = #{courseIds}, goods_ids = #{goodsIds}, total_price = #{totalPrice}
where id = #{id}
