mapper.select:
		SELECT 
			id, course_id, member_id, classroom_id, start_date, end_date, start_time, end_time, min_num, max_mun, status 		
		FROM t_curriculum 



		  		  	AND id = #{id}   


		  		  	AND course_id = #{courseId}   


		  		  	AND member_id = #{memberId}   


		  		  	AND classroom_id = #{classroomId}   


		  		  	AND start_date LIKE '%${ startDate}%'  


		  		  	AND end_date LIKE '%${ endDate}%'  


		  		  	AND start_time LIKE '%${ startTime}%'  


		  		  	AND end_time LIKE '%${ endTime}%'  


		  		  	AND min_num = #{minNum}   


		  		  	AND max_mun = #{maxMun}   


		  		  	AND status LIKE '%${ status}%'  





                ORDER BY ${sort} ${order}


                ORDER BY id DESC

select 
	*  
from t_curriculum



  		  	AND id = #{id}   


  		  	AND course_id = #{courseId}   


  		  	AND member_id = #{memberId}   


  		  	AND classroom_id = #{classroomId}   


  		  	AND start_date LIKE '%${ startDate}%'  


  		  	AND end_date LIKE '%${ endDate}%'  


  		  	AND start_time LIKE '%${ startTime}%'  


  		  	AND end_time LIKE '%${ endTime}%'  


  		  	AND min_num = #{minNum}   


  		  	AND max_mun = #{maxMun}   


  		  	AND status LIKE '%${ status}%'

mapper.update:
update t_curriculum
course_id = #{courseId}, member_id = #{memberId}, classroom_id = #{classroomId}, start_date = #{startDate}, end_date = #{endDate}, start_time = #{startTime}, end_time = #{endTime}, min_num = #{minNum}, max_mun = #{maxMun}, status = #{status}
where id = #{id}
